/*
438. Find All Anagrams in a String
Medium

Given two strings s and p, return an array of all the start indices of p's anagrams in s. You may return the answer in any order.

An Anagram is a word or phrase formed by rearranging the letters of a different word or phrase, typically using all the original letters exactly once.

 

Example 1:

Input: s = "cbaebabacd", p = "abc"
Output: [0,6]
Explanation:
The substring with start index = 0 is "cba", which is an anagram of "abc".
The substring with start index = 6 is "bac", which is an anagram of "abc".

Example 2:

Input: s = "abab", p = "ab"
Output: [0,1,2]
Explanation:
The substring with start index = 0 is "ab", which is an anagram of "ab".
The substring with start index = 1 is "ba", which is an anagram of "ab".
The substring with start index = 2 is "ab", which is an anagram of "ab".

 

Constraints:

    1 <= s.length, p.length <= 3 * 104
    s and p consist of lowercase English letters.

Accepted
412,885
Submissions
887,072
*/

#include <bits/stdc++.h>
#include <vector>
#include <map>
#include <algorithm>
using namespace std;

class Solution {
public:
    void print(const map<char,int>& mmap) {
        for(auto itr=mmap.begin();itr!=mmap.end();itr++)
            cout << itr->first << ", " << itr->second << endl;
        cout << endl;
    }
    bool compareMaps(const map<char,int>& lhs,const map<char,int>& rhs) {
        //using std::equal
        return (
                (lhs.size() == rhs.size()) &&
                (std::equal(lhs.begin(),lhs.end(),rhs.begin()))
               );
    }
    
    vector<int> findAnagrams(string s, string p) {
        if(p.size() > s.size())
            return {};
        
        vector<int> retIndices;
        map<char,int> PordMap, SordMap;
        
        for(int i=0;i<p.size();i++) {
            PordMap[p[i]]++;
            SordMap[s[i]]++;
        }
        
        //found first p size characters in p
        // to be same as first p size characters in s
        if(compareMaps(SordMap,PordMap))
            retIndices.emplace_back(0);
        
        // print(SordMap);
        int start = 0;
        int end = p.size()-1;
        
        // while(start <= end && end <= s.size()) {
        while(start < s.size()) {
            SordMap[s[++end]]++;
            // cout << "before: " << s[start] << ", " << SordMap[s[start]] << endl;
            SordMap[s[start]]--;
            // cout << "after: " << s[start] << ", " << SordMap[s[start]] << endl;
            //reduce count when moving forward
            //if size of that char in map is 0, erase
            if(SordMap[s[start]] == 0)
                SordMap.erase(s[start]);
            start++;
            cout << start << endl;
            // print(SordMap);
            if(compareMaps(SordMap,PordMap))
                retIndices.emplace_back(start);
        }
        
        
        return retIndices;
    }
};

int main() {
    string s = 
// "";
"";
    string p = "yqrbgjdwtcaxzsnifvhmou";
    unique_ptr<Solution> sol = make_unique<Solution>();
    auto retInx = sol->findAnagrams(s,p);
    for(auto itr=retInx.begin();itr!=retInx.end();itr++)
        cout << *itr << "\t";
    cout << endl;
    return 0;
}